package operadores;

public class Aritmeticos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 4;
		int y = 2;
		int a = 3;
		int resultado;
		
		resultado = x + (y * a);
		
		x += y;
		
		System.out.println("O resultado é: " + resultado);
		System.out.println("O resultado é: " + x);
	}

}
